import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [players, setPlayers] = useState([]);
  const [name, setName] = useState('');
  const [playerA, setPlayerA] = useState('');
  const [playerB, setPlayerB] = useState('');
  const [result, setResult] = useState('');

  useEffect(() => {
    // Fetch the leaderboard when the app loads
    fetchLeaderboard();
  }, []);

  const fetchLeaderboard = async () => {
    try {
      const response = await axios.get('http://localhost:5000/leaderboard');
      setPlayers(Object.entries(response.data).map(([name, rating]) => ({ name, rating })));
    } catch (error) {
      console.error('Error fetching leaderboard', error);
    }
  };

  const addPlayer = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/add_player', { name });
      setName('');
      fetchLeaderboard();
    } catch (error) {
      console.error('Error adding player', error);
    }
  };

  const updateRating = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/update_rating', { player_a: playerA, player_b: playerB, result });
      setPlayerA('');
      setPlayerB('');
      setResult('');
      fetchLeaderboard();
    } catch (error) {
      console.error('Error updating ratings', error);
    }
  };

  return (
    <div>
      <h1>Elo Tracker</h1>

      {/* Add Player Form */}
      <h2>Add Player</h2>
      <form onSubmit={addPlayer}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter player name"
        />
        <button type="submit">Add Player</button>
      </form>

      {/* Leaderboard */}
      <h2>Leaderboard</h2>
      <ul>
        {players.map((player) => (
          <li key={player.name}>{player.name}: {player.rating}</li>
        ))}
      </ul>

      {/* Update Rating Form */}
      <h2>Update Rating</h2>
      <form onSubmit={updateRating}>
        <input
          type="text"
          value={playerA}
          onChange={(e) => setPlayerA(e.target.value)}
          placeholder="Player A name"
        />
        <input
          type="text"
          value={playerB}
          onChange={(e) => setPlayerB(e.target.value)}
          placeholder="Player B name"
        />
        <select value={result} onChange={(e) => setResult(e.target.value)}>
          <option value="">Select result</option>
          <option value="1">Player A Wins</option>
          <option value="0">Player B Wins</option>
          <option value="0.5">Draw</option>
        </select>
        <button type="submit">Update Rating</button>
      </form>
    </div>
  );
}

export default App;
